      render(){
          return(
            <div>
              <h1>SAVE THE WORLD</h1>
            <form>
              <label>
                Username:  
                <input name="username" type="text" />
              </label>
              <label>
                Password:  
                <input name="password" type="text" />
              </label>
              <button type="submit">Login</button>
            </form>
            </div>
          );
      }

}

export default LoginPage;
